import React from "react";
import { useFormik } from "formik";
import styles from "./form.module.css";

const validate = values => {
  const errors = {};
  if (!values.firstName) {
    errors.firstName = "Pole nie może być puste";
  } else if (values.firstName.length > 15) {
    errors.firstName = "Maksymalna ilość znaków to 15";
  }

  if (!values.lastName) {
    errors.lastName = "Pole nie może być puste";
  } else if (values.lastName.length > 20) {
    errors.lastName = "Maksymalna ilość znaków to 20";
  }

  if (!values.email) {
    errors.email = "Pole nie może być puste";
  } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
    errors.email = "Nieprawidłowy adres e-mail";
  }

  if (!values.phone) {
    errors.phone = "Pole nie może być puste";
  } else if (values.phone.length > 12) {
    errors.phone = "Maksymalna ilość znaków to 12";
  }

  return errors;
};

const SignupForm = () => {
  const formik = useFormik({
    initialValues: {
      firstName: "",
      lastName: "",
      email: "",
      phone: "",
      password: ""
    },
    validate,
    onSubmit: values => {
      alert(JSON.stringify(values, null, 2));
    }
  });

  return (
    <form onSubmit={formik.handleSubmit} className={styles.form}>
      <label htmlFor="firstName" className={styles.label}>
        Imię
      </label>
      <input
        id="firstName"
        name="firstName"
        type="text"
        onChange={formik.handleChange}
        onBlur={formik.handleBlur}
        value={formik.values.firstName}
        className={styles.textInput}
      />
      {formik.touched.firstName && formik.errors.firstName ? (
        <p className={styles.error}>{formik.errors.firstName}</p>
      ) : null}

      <label htmlFor="lastName" className={styles.label}>
        Nazwisko
      </label>
      <input
        id="lastName"
        name="lastName"
        type="text"
        onChange={formik.handleChange}
        onBlur={formik.handleBlur}
        value={formik.values.lastName}
        className={styles.textInput}
      />
      {formik.touched.lastName && formik.errors.lastName ? (
        <p className={styles.error}>{formik.errors.lastName}</p>
      ) : null}

      <label htmlFor="email" className={styles.label}>
        Adres e-mail
      </label>
      <input
        id="email"
        name="email"
        type="email"
        onChange={formik.handleChange}
        onBlur={formik.handleBlur}
        value={formik.values.email}
        className={styles.textInput}
      />
      {formik.touched.email && formik.errors.email ? (
        <p className={styles.error}>{formik.errors.email}</p>
      ) : null}

      <label htmlFor="phone" className={styles.label}>
        Telefon
      </label>
      <input
        id="phone"
        name="phone"
        type="text"
        onChange={formik.handleChange}
        onBlur={formik.handleBlur}
        value={formik.values.phone}
        className={styles.textInput}
      />
      {formik.touched.phone && formik.errors.phone ? (
        <p className={styles.error}>{formik.errors.phone}</p>
      ) : null}

      <label htmlFor="password" className={styles.label}>
        Hasło
      </label>
      <div>
        <input
          id="password"
          name="password"
          type="password"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.password}
          className={styles.textInput}
        />
        <button>Pokaż</button>
      </div>

      {formik.touched.password && formik.errors.password ? (
        <p className={styles.error}>{formik.errors.password}</p>
      ) : null}

      <button type="submit" className={`btn main-btn ${styles.submitBtn}`}>
        Zarejestruj się
      </button>
    </form>
  );
};

export default SignupForm;
